<style lang="scss">
.fl-main-feature {
  background: var(--p-white-color);
  padding: 100px 0px;
  &__content {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 30px;
  }
  &__item {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;

    svg {
      width: 60px;
      height: 60px;
      margin-bottom: 10px !important;
    }
    h1 {
      font-size: var(--p-title-font);
      font-weight: 700;
      color: var(--p-black-color);
      font-style: italic;
      position: relative;
      margin-bottom: 15px !important;
    }
    span {
      height: 4px;
      width: 70px;
      background: var(--fl-primary);
      margin-bottom: 15px !important;
    }
    p {
      color: var(--p-black-color) !important;
      font-size: var(--p-small-font);
      font-weight: 400;
    }
  }

  @media screen and (max-width: 768px) {
    padding: 60px 0px;
    &__content {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: 10px;
    }
    &__item {
      svg {
        width: 40px;
        height: 40px;
        margin-bottom: 10px !important;
      }
      h1 {
        font-size: 16px;
        font-weight: 600;
        margin-bottom: 10px !important;
      }
      span {
        height: 3px;
        max-width: 70px;
        width: 100%;
        margin-bottom: 10px !important;
      }
      p {
        color: var(--p-black-color) !important;
        font-size: var(--p-small-font);
        font-weight: 400;
        line-height: 150% !important;
      }
    }
  }

  @media screen and (min-width: 769px) and (max-width: 1200px) {
    background: var(--p-white-color);
    padding: 70px 0px;
    &__content {
      display: grid;
      grid-template-columns: repeat(4, 1fr);
      gap: 20px;
    }
    &__item {
      svg {
        width: 50px;
        height: auto;
      }
      h1 {
        font-size: 18px;
        font-weight: 600;
      }
      span {
        height: 4px;
        width: 70px;
        background: var(--fl-primary);
        margin-bottom: 15px !important;
      }
      p {
        font-size: 14px;
      }
    }
  }
}
</style>
